import React, { Component } from 'react';
import './UserInput.css';
import Hasil from '../component/Hasil'

class UserInput extends Component {

    constructor(props) {
        super(props)
        this.state = {
            algo: "BS",
            numbers: "",
            hasil: []
        }
    }
    refreshPage = () => {
        window.location.reload(false);
    }
    handleSubmit = (event) => {
        event.preventDefault();
        if (this.state.numbers != '') {
            fetch("http://localhost:5000/sorting-web", {
                method: "POST",
                headers: { "Content-type": "application/json" },
                body: JSON.stringify({
                    'numbers': this.state.numbers,
                    'algo': this.state.algo
                })
            }).then(e => e.json()).then(data => this.setState({ hasil: data.data })).catch((err) => alert(err))

        } else {
            alert("masukin angkanya geng!")
        }
    }

    handleChangeAlgo = (event) => {
        event.preventDefault();
        this.setState({
            algo: event.target.value
        });
    }
    handleChangeNumber = (event) => {
        event.preventDefault();
        this.setState({
            numbers: event.target.value
        })

    }
    render() {
        return (
            <div>
                {/* masukkan angka-angka untuk diolah */}
                <div>
                    <body className="App-insert">
                        <p style={{ fontSize: 40, textAlign: "center" }}>Insert ur number</p>
                        <form>
                            <p>
                                <input
                                    type="text"
                                    placeholder="example :1,2,3,4,5"
                                    onChange={this.handleChangeNumber}
                                />
                            </p>
                        </form>
                    </body>
                </div>
                {/* pilih algoritma sorting */}
                <div>
                    <body className="App-choose">
                        <p style={{ fontSize: 40, textAlign: "center" }}>Choose ur algo</p>
                        <form>
                            <label>
                                <select style={{ blockSize: 20 }} onChange={this.handleChangeAlgo} >
                                    <option value="BS">Bubble Sort</option>
                                    <option value="MS">Merge Sort</option>
                                </select>
                            </label>
                        </form>

                        <div>
                            <button style={{ backgroundColor: '#3593B5', fontSize: 15, fontWeight: "bold", blockSize: 20 }} onClick={this.handleSubmit}>Submit</button>
                        </div>
                    </body>
                </div>
                {/* nampilin hasil */}
                <div>
                    {this.state.hasil.map((hasilSorting) => (
                        <div>
                            <Hasil hasilSort={hasilSorting} />
                        </div>
                    ))}
                </div>
            </div>
        );
    }
}

export default UserInput;
