import React, { Component } from 'react';
import './Hasil.css';

class Hasil extends Component {
    constructor(props) {
        super(props)
        this.state = {
            step: -1,
            result: props.hasilSort
        }
    }
    render() {
        return (
            <div>
                <body className="App-hasil">
                    <p>Step {this.state.step + 1} :</p>
                    <div className="hasil-angka">
                        <p style={{ textAlign: "center" }}>{this.state.result}</p>
                    </div>
                </body>
            </div>
        );
    }
}

export default Hasil;
