import React, { Component } from 'react';
import './Hasil.css';

class Hasil extends Component {
    constructor(props) {
        super(props)
        this.step = props.hasilSort.step
        this.result = []

    }
    setResult = (props) => {
        this.setState({
            result: props.hasilSort.result
        })
    }
    render() {
        const { array } = this.result
        return (
            <div>
                <this.setResult />
                <body className="App-hasil">
                    <p>Step {this.step} :</p>
                    <div className="hasil-angka">
                        {/* {array.map((value, key) => (
                            <div key={key}>
                                <p className="tampilan-result">[ {value} ]</p>
                            </div>
                        ))} */}
                        <p className="tampilan-result">[ {this.result} ]</p>
                    </div>
                </body>
            </div>
        );
    }
}

export default Hasil;
