import React, { Component } from 'react';
import './ChooseAlgo.css';

class ChooseAlgo extends Component {
    state = {
        algo: 'BS'
    }
    handleSubmit() {
        alert('hayolo: ' + this.state.algo);
    }
    handleChange() {
        this.setState({ algo: event.target.algo });
    }
    render() {
        return (
            <div>
                <body className="App-choose">
                    <p style={{ fontSize: 40, textAlign: "center" }}>Choose ur algo</p>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            <select style={{ blockSize: 20 }} algo={this.state.algo} onChange={this.handleChange} >
                                <option algo='BS'>Bubble Sort</option>
                                <option algo='MS'>Merge Sort</option>
                            </select>
                        </label>
                        <br></br>
                        <input style={{ blockSize: 20 }} type="submit" value="Submit" />
                    </form>
                </body>
            </div>
        );
    }
}

export default ChooseAlgo;
