import React, { Component } from 'react';
import './Hasil.css';

class Hasil extends Component {
    constructor(props) {
        super(props)
        this.step = props.hasilSort.step
        this.result = props.hasilSort.result
        this.arrayResult = []

    }

    render() {
        this.setArrayResult(this.result)
        const { array } = this.arrayResult

        return (
            <div>

                <body className="App-hasil">
                    <p>Step {this.step} :</p>
                    <div className="hasil-angka">
                        {/* {array.map((value, key) => (
                            <div key={key}>
                                <p className="tampilan-result">[ {value} ]</p>
                            </div>
                        ))} */}
                        <p className="tampilan-result">[ {this.arrayResult} ]</p>
                    </div>
                </body>
            </div>
        );
    }
}

export default Hasil;
