import React, { Component } from 'react';
import './ChooseAlgo.css';

class ChooseAlgo extends Component {

    constructor(props) {
        super(props);
        this.state = {
            algo: 'BS'
        }
    }
    handleSubmit(event) {
        event.preventDefault();
        alert('hayolo: ' + this.state.algo);

    }
    handleChange(event) {
        event.preventDefault();
        this.setState({
            algo: event.target.value
        });
    }
    render() {
        return (
            <div>
                <body className="App-choose">
                    <p style={{ fontSize: 40, textAlign: "center" }}>Choose ur algo</p>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            <select style={{ blockSize: 20 }} onChange={this.handleChange} >
                                <option value='BS'>Bubble Sort</option>
                                <option value='MS'>Merge Sort</option>
                            </select>
                        </label>
                        <br></br>
                        <input style={{ blockSize: 20 }} type="submit" value="Submit" />
                    </form>
                </body>
            </div>
        );
    }
}

export default ChooseAlgo;
