import React from 'react';
import logo from './logo.svg';
import './css/App.css';
import Form from './Form.js';

class App extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      len : 0,
      arr : [],
      sorted : []
    }
    this.setLenArr = this.setLenArr.bind(this)
  }

  setLenArr(length,array) {
    this.setState({len : length})
    this.state.arr.forEach(e => 
      this.state.arr.pop()
    )
    array.forEach(element => {
        this.state.arr.push(element)
    });
  }

  render(){
    return (
    <div className="App">
        <div>
            <h1>
                SORTING TUTORIALLLLL
            </h1>
        </div>
        <div>
            <Form onSub={this.setLenArr}/>
        </div>
        <div>
            {this.state.len ? this.state.len : "NON"}
        </div>
        <div>
            {this.state.arr.map(e => e)}
        </div>
  </div>
  );
  };
}

export default App;
