import React from 'react';





export default class Form extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            length : 0,
            arr : []
        }
        this.setFunc = this.setFunc.bind(this)
        this.onChange = this.onChange.bind(this)
    }

    setFunc () {
        this.props.onSub(this.state.length,this.arr)
    }

    onChange (e){
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

    parseArr(e){
        var arrtext = e.target.value;

    }


    render (){
        return(
            <div>
                <h4>
                    Please enter data
                </h4>
                <div>
                    <input name="length" type="number" value={this.state.length} onChange={this.onChange} />
                    <input name="arrText" type="text" value={this.state.arrText} onChange={this.onChange}/>
                </div>
            </div>
        )
    }
}
