import React from 'react'
import * as d3 from 'd3';

export default class Visual extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            len : this.props.length,
            arr : this.props.array
        }
        this.drawArr = this.drawArr.bind(this)
        // this.mergeSort = this.mergeSort.bind(this)
        // this.bubbleSort = this.bubbleSort.bind(this)
    }



    drawArr(){
        // return(
        var space = document.getElementById("space")
        var svg = d3.select("space").append("svg").attr("width",250).attr("height",250);
        var rects = svg.selectAll("rect").data(this.state.arr).enter().append("rect");
        rects.attr("id",function(d){return "elm" + d})
            .attr("width",15)
            .attr("height",function(d){return d*15})
        // )
    }

    render(){
        return (
            <div id="space">
                {this.drawArr()}
            </div>
        )
    }
}
