import React from 'react'
import * as d3 from 'd3';

export default class Visual extends React.Component{
    constructor(props){
        super(props);
        // console.log(props)
        this.state = {
            len : props.length,
            arr : props.array
        }
        this.drawArr = this.drawArr.bind(this)
        // this.mergeSort = this.mergeSort.bind(this)
        // this.bubbleSort = this.bubbleSort.bind(this)
        // this.drawArr();
    }

    addCircle(){
    }

    drawArr(){
        console.log(this.state.arr)
        const data = this.state.arr;
        var x =10
        return(
            <svg viewBox = "0 0 100 100">
                {this.state.arr.map(e => 
                    this.elm(x+10,50,20,e*10)
                )}
            </svg>
        )
    }

    elm(xx,yy,w,h){
        return (
            <svg>
                <rect x = {xx} y = {yy} width = {w} height = {h} style={{fill : '#FFFFFF'}}/>
            </svg>
        )
    }

    svg(){
        return (
            <svg style ={{border : "2px solid black"}}/>
        )
    }

    circle(){
        return(
            <svg>
                <circle cx="150" cy="77" r="40" />
            </svg>
        )
    }


    render(){
        return (
            <div>
                <br/>
                <br/>
                <br/>
                <div id="space">
                    {/* {this.circle()} */}
                    {this.drawArr()}
                </div>
                <div>
                    <button onClick={this.addCircle}>Add Circle</button>
                </div>
            </div>
        )
    }
}
